var affiliateClass = function(){
    this.buildChart = function(id, info, label) {
        var data = [
            {
                value: info[0],
                color: 'rgba(246,47,52,.8)',
                highlight: 'rgba(246,47,52,1)',
                label: label[0]
            }, {
                value: info[1],
                color: 'rgba(52,174,172,.8)',
                highlight: 'rgba(52,174,172,1)',
                label: label[1]
            }, {
                value: info[2],
                color: 'rgba(253,172,75,.8)',
                highlight: 'rgba(253,172,75,1)',
                label: label[2]
            }
        ];

        var ctx = $('#' + id).get(0).getContext('2d');
        new Chart(ctx).Doughnut(data, {
            animationEasing: 'easeOutQuart'
        });
    }
}

var affiliateJS = new affiliateClass();
